import React, { Component } from 'react';
import Loading from '../pages/Loading';

class MusicCard extends Component {
  state = {
    cheacked: false,
  };

  render() {
    const { previewUrl, trackName, trackId, cheacked, addFavorite } = this.props;
    const { isLoading } = this.state;
    return (
      <div>
        {isLoading && <Loading />}

        <h3>{ trackName }</h3>
        <audio data-testid="audio-component" src={ previewUrl } controls>
          <track kind="captions" />
          <code>audio</code>
          .
        </audio>
        <label
          data-testid={ `checkbox-music-${trackId}` }
          htmlFor={ `favoriteMusic-${trackId}` }
        >
          <input
            type="checkbox"
            name="favoriteMusic"
            id="favoriteMusic"
            value={ cheacked }
            onChange={ addFavorite }
          />
          Favorita
        </label>

      </div>

    );
  }
}

export default MusicCard;
