import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';
import { createUser } from '../services/userAPI';
import Loading from './Loading';

class Login extends Component {
  state = {
    nameLogin: '',
    isSavedButtonDisable: true,
    isLoading: false,
    login: false,
  };

  onInputChange = ({ target }) => {
    const { name } = target;
    const { value } = target;
    this.setState({ [name]: value }, () => {
      const { nameLogin } = this.state;
      const validationRole = 2;
      const newStatusButton = nameLogin.length <= validationRole;
      this.setState({
        isSavedButtonDisable: newStatusButton,
      });
    });
  };

  saveLogin = async (event) => {
    event.preventDefault();
    const { nameLogin } = this.state;
    this.setState({
      isLoading: true,
    });
    await createUser({ nameLogin });
    this.setState({
      isLoading: false,
      login: true,
    });
  };

  render() {
    const { isSavedButtonDisable, nameLogin, isLoading, login } = this.state;
    return (
      <div data-testid="page-login">
        { isLoading ? <Loading /> : (
          <form action="">
            <label htmlFor="name">
              Nome:
              <input
                type="text"
                name="nameLogin"
                id="name"
                data-testid="login-name-input"
                onChange={ this.onInputChange }
              />
            </label>
            <button
              type="submit"
              data-testid="login-submit-button"
              disabled={ isSavedButtonDisable }
              onClick={ this.saveLogin }
            >
              Entrar
            </button>
          </form>
        )}
        {login && <Redirect to="/search" />}
      </div>
    );
  }
}

export default Login;
