import React, { Component } from 'react';
import Loading from '../pages/Loading';

class MusicCard extends Component {
  state = {
    isChecked: false,
    isLoading: false,
  };

  favoriteButton = async ({ event, trackId }) => {
    const { checked } = event.target;
    const { musics } = this.state;
    this.setState({
      isLoading: true,
    });
    const song = musics.find((music) => music.trackId === trackId);
    if (checked) {
      await addSong(song);
    }
    this.setState({
      isLoading: false,
    });
  };

  render() {
    const { previewUrl, trackName, trackId, cheacked, addFavorite } = this.props;
    const { isLoading } = this.state;
    return (
      <div>
        {isLoading && <Loading />}

        <h3>{ trackName }</h3>
        <audio data-testid="audio-component" src={ previewUrl } controls>
          <track kind="captions" />
          <code>audio</code>
          .
        </audio>
        <label
          data-testid={ `checkbox-music-${trackId}` }
          htmlFor={ `favoriteMusic-${trackId}` }
        >
          <input
            type="checkbox"
            name="favoriteMusic"
            id="favoriteMusic"
            value={ cheacked }
            onChange={ async (event) => {
              await addFavorite({ event, trackId });
              this.setState({
                cheacked: event.target.checked,
              });
            } }
          />
          Favorita
        </label>

      </div>

    );
  }
}

export default MusicCard;
