import React, { Component } from 'react';
import Header from '../components/Header';
import MusicCard from '../components/MusicCard';
import { addSong } from '../services/favoriteSongsAPI';
import getMusics from '../services/musicsAPI';

class Album extends Component {
  state = {
    musics: [],
    isLoading: false,
  };

  async componentDidMount() {
    this.musicsApi();
  }

  musicsApi = async () => {
    const { match: { params: { id } } } = this.props;
    const musicReturn = await getMusics(id);
    this.setState(({
      musics: musicReturn,
    }));
  };

  favoriteButton = async ({ event, trackId }) => {
    const { checked } = event.target;
    const { musics } = this.state;
    this.setState({
      isLoading: true,
    });
    const song = musics.find((music) => music.trackId === trackId);
    if (checked) {
      await addSong(song);
    }
    this.setState({
      isLoading: false,
    });
  };

  render() {
    const { musics, isLoading } = this.state;
    return (
      <div data-testid="page-album">
        <Header />
        <h3 data-testid="artist-name">
          { musics[0] && musics[0].artistName }
        </h3>
        <h4 data-testid="album-name">

          { musics[0] && musics[0].collectionName }
        </h4>
        { musics.map((song, index) => (index > 0 && <MusicCard
          previewUrl={ song.previewUrl }
          key={ song.trackId }
          trackName={ song.trackName }
          trackId={ song.trackId }
          musics={ song }
          addFavorite={ this.addFavorite }
        />))}
      </div>
    );
  }
}

export default Album;
