import React, { Component } from 'react';
import Header from '../components/Header';
import searchAlbumsAPI from '../services/searchAlbumsAPI';
import Loading from './Loading';

class Search extends Component {
  state = {
    artist: '',
    isSearchButtonDisable: true,
    isLoading: false,
    nameExibition: '',
    colectionMusic: [],
  };

  onInputChange = ({ target }) => {
    const { name } = target;
    const { value } = target;
    this.setState({ [name]: value }, () => {
      const { artist } = this.state;
      const validationRole = 1;
      const newStatusButton = artist.length <= validationRole;
      this.setState({
        isSearchButtonDisable: newStatusButton,
      });
    });
  };

  searchButton = async (event) => {
    event.preventDefault();
    const { artist } = this.state;
    this.setState({
      isLoading: true,
      nameExibition: artist,
    });
    const artistReturn = await searchAlbumsAPI(artist);
    this.setState({
      artist: '',
      isLoading: false,
    });
    console.log(artistReturn.artistName);
  };

  render() {
    const {
      isSearchButtonDisable,
      isLoading, nameExibition,
      colectionMusic,
    } = this.state;
    return (
      <div data-testid="page-search">
        <Header />
        <div>
          {isLoading ? <Loading /> : (
            <form>
              <label htmlFor="artist">
                <input
                  type="text"
                  name="artist"
                  id="artist"
                  data-testid="search-artist-input"
                  onChange={ this.onInputChange }
                />
              </label>
              <button
                type="submit"
                data-testid="search-artist-button"
                disabled={ isSearchButtonDisable }
                onClick={ this.searchButton }
              >
                Pesquisar

              </button>

            </form>

          )}
          <div>
            <p>
              Resultado de Ã¡lbuns de:
              {nameExibition}
            </p>
          </div>
          <div>
            {colectionMusic.map((colection) => {
              <card>
                <p>{colection.artistName}</p>
              </card>;
            })}
            <card>teste</card>
          </div>

        </div>
      </div>
    );
  }
}

export default Search;
