import React, { Component } from 'react';
import Loading from '../pages/Loading';

class MusicCard extends Component {
  state = {
    isLoading: false,
    cheacked: false,
  };

  favoriteButton = async (event) => {
    const { addFavorite, musics } = this.props;
    const { checked } = event.target;
    this.setState({
      isLoading: true,
      cheacked: checked,
    });
    if (checked) {
      await addFavorite(musics);
    }
    this.setState({
      isLoading: false,
    });
  };

  render() {
    const { previewUrl, trackName, trackId, cheacked } = this.props;
    const { isLoading } = this.state;
    return (
      <div>

        <h3>{ trackName }</h3>
        <audio data-testid="audio-component" src={ previewUrl } controls>
          <track kind="captions" />
          <code>audio</code>
          .
        </audio>
        <label data-testid={ `checkbox-music-${trackId}` } htmlFor="favoriteMusic">
          <input
            type="checkbox"
            name="favoriteMusic"
            id="favoriteMusic"
            value={ cheacked }
            onChange={ this.favoriteButton }
          />
          Favorita
        </label>
        {isLoading && <Loading />}
      </div>

    );
  }
}

export default MusicCard;
