import React, { Component } from 'react';
import Header from '../components/Header';
import searchAlbumsAPI from '../services/searchAlbumsAPI';
import Loading from './Loading';

class Search extends Component {
  state = {
    artist: '',
    isSearchButtonDisable: true,
    isLoading: false,
  };

  onInputChange = ({ target }) => {
    const { name } = target;
    const { value } = target;
    this.setState({ [name]: value }, () => {
      const { artist } = this.state;
      const validationRole = 1;
      const newStatusButton = artist.length <= validationRole;
      this.setState({
        isSearchButtonDisable: newStatusButton,
      });
    });
  };

  searchButton = async (event) => {
    event.preventDefault();
    const { artist } = this.state;
    this.setState({
      isLoading: true,
    });
    const artistReturn = await searchAlbumsAPI(artist);
    this.setState({
      artist: '',
      isLoading: false,
    });
    console.log(artistReturn);
  };

  render() {
    const { isSearchButtonDisable, isLoading } = this.state;
    return (
      <div data-testid="page-search">
        <Header />
        <div>
          {isLoading ? <Loading /> : (
            <form>
              <label htmlFor="artist">
                <input
                  type="text"
                  name="artist"
                  id="artist"
                  data-testid="search-artist-input"
                  onChange={ this.onInputChange }
                />
              </label>
              <button
                type="submit"
                data-testid="search-artist-button"
                disabled={ isSearchButtonDisable }
                onClick={ this.searchButton }
              >
                Pesquisar

              </button>

            </form>

          )}

        </div>
      </div>
    );
  }
}

export default Search;
