import React, { Component } from 'react';
import Header from '../components/Header';
import searchAlbumsAPI from '../services/searchAlbumsAPI';

class Search extends Component {
  state = {
    artist: '',
    isSearchButtonDisable: true,
    isLoading: false,
  };

  onInputChange = ({ target }) => {
    const { name } = target;
    const { value } = target;
    this.setState({ [name]: value }, () => {
      const { artist } = this.state;
      const validationRole = 1;
      const newStatusButton = artist.length <= validationRole;
      this.setState({
        isSearchButtonDisable: newStatusButton,
      });
    });
  };

  searchButton = async (event) => {
    event.preventDefault();
    const { artist } = this.state;
    this.setState({
      isLoading: true,
    });
    await searchAlbumsAPI(artist);
    this.setState({
      artist: '',
    });
  };

  render() {
    const { isSearchButtonDisable, isLoading } = this.state;
    return (
      <div data-testid="page-search">
        <Header />
        <div>
          <label htmlFor="artist">
            <input
              type="text"
              name="artist"
              id="artist"
              data-testid="search-artist-input"
              onChange={ this.onInputChange }
            />
          </label>
          <button
            type="submit"
            data-testid="search-artist-button"
            disabled={ isSearchButtonDisable }
          >
            Pesquisar

          </button>

        </div>
      </div>
    );
  }
}

export default Search;
