import React, { Component } from 'react';
import Loading from '../pages/Loading';
import addSong from '../services/favoriteSongsAPI';

class MusicCard extends Component {
  state = {
    isLoading: false,
    cheacked: false,
  };

  favoriteButton = async () => {
    const { addFavorite } = this.props;
    this.setState({
      isLoading: true,
    });
    this.setState((oldState) => ({
      cheacked: !oldState.cheacked,
    }), async () => {
      const { cheacked } = this.state;
      if (cheacked) {
        await addFavorite();
        this.setState({
          isLoading: false,
        });
      }
    });
  };

  render() {
    const { previewUrl, trackName, trackId, cheacked } = this.props;
    const { isLoading } = this.state;
    return (
      <div>
        {isLoading && <Loading />}
        <h3>{ trackName }</h3>
        <audio data-testid="audio-component" src={ previewUrl } controls>
          <track kind="captions" />
          <code>audio</code>
          .
        </audio>
        <label data-testid={ `checkbox-music-${trackId}` } htmlFor="favoriteMusic">
          <input
            type="checkbox"
            name="favoriteMusic"
            id="favoriteMusic"
            value={ cheacked }
            onChange={ this.favoriteButton }
          />
          Favorita
        </label>
      </div>

    );
  }
}

export default MusicCard;
