import React, { Component } from 'react';
import { createUser } from '../services/userAPI';

class Login extends Component {
  state = {
    nameLogin: '',
    isSavedButtonDisable: true,
  };

  onInputChange = ({ target }) => {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    this.setState({ [name]: value }, () => {
      const { nameLogin } = this.state;
      const validationRole = 2;
      const newStatusButton = nameLogin.length <= validationRole;
      this.setState({
        isSavedButtonDisable: newStatusButton,
      });
    });
  };

  render() {
    const { isSavedButtonDisable, nameLogin } = this.state;
    return (
      <div data-testid="page-login">
        <form action="">
          <label htmlFor="name">
            Nome:
            <input
              type="text"
              name="nameLogin"
              id="name"
              value={ nameLogin }
              data-testid="login-name-input"
              onChange={ this.onInputChange }
            />
          </label>
          <button
            type="submit"
            data-testid="login-submit-button"
            disabled={ isSavedButtonDisable }
            onClick={ createUser(`{ name: ${nameLogin.split('=')[1]} }`) }
          >
            Entrar
          </button>
        </form>
      </div>
    );
  }
}

export default Login;
