import React, { Component } from 'react';

class MusicCard extends Component {
  state = {
    renderMusicPlayer: false,
  };

  validation = () => {
    const { previewUrl } = this.props;
    const validation = previewUrl === undefined;
    this.setState(({
      renderMusicPlayer: validation,
    }));
  };

  render() {
    const { previewUrl } = this.props;
    const { renderMusicPlayer } = this.state;
    return (
      <div>

        { renderMusicPlayer ? ''

          : <div>
            <audio data-testid="audio-component" src={ previewUrl } controls>
              <track kind="captions" />
              <code>audio</code>
            </audio>
          </div>
          }
      </div>

    );
  }
}

export default MusicCard;
